WHERE USER_ID = #{userId}
AND VALIDATION_CODE = #{validationCode}
AND EXPIRE_FLAG = #{expireFlag}

SELECT * FROM cqupt_validation_code
WHERE USER_ID = #{userId}
AND EXPIRE_FLAG = #{expireFlag}

mapper.update:
